import './Navbar.sass'
import { Container } from 'react-bootstrap';
import { NavLink } from 'react-router-dom';
import useMediaQuery from 'hooks/useMediaQuery';
import { useState } from 'react';
import { ReactComponent as Burger } from 'assets/img/burger.svg';
import { ReactComponent as Close } from 'assets/img/close.svg';

const Navbar = () => {
  let media = useMediaQuery(768)
  const [showList, setShowList] = useState(false);
  return (
    <nav className="menu">
      <Container>
        <div className="menu__wrapper">
          <h1 className="menu__logo">StellarGame</h1>
          {media.greaterThanQuery ?
          <ul className="menu__links">
            <li className="menu__link"><NavLink to='/'>Menu</NavLink></li>
            <li className="menu__link"><NavLink to='/about'>About us</NavLink></li>
            <li className="menu__link"><NavLink to='/contacts'>Contacts</NavLink></li>
          </ul>
          :
          showList ?
          <ul className="menu__links menu__links_mobile">
            <div className="menu__links_mobile-close" onClick={() => setShowList(false)}><Close /></div>
            <li className="menu__link" onClick={() => setShowList(false)}><NavLink to='/'>Menu</NavLink></li>
            <li className="menu__link" onClick={() => setShowList(false)}><NavLink to='/about'>About us</NavLink></li>
            <li className="menu__link" onClick={() => setShowList(false)}><NavLink to='/contacts'>Contacts</NavLink></li>
          </ul>
          :
          <div className="menu__burger" onClick={() => setShowList(true)}><Burger /></div>
          }
        </div>
      </Container>
    </nav>
  )
};

export default Navbar;
