import { useEffect, useState } from "react"

const useMediaQuery = (query) => {
  const [greaterThanQuery, setGreaterThanQuery] = useState(window.innerWidth > query);

  const onResize = (e) => {
    setGreaterThanQuery(e.target.innerWidth > query)
  }

  useEffect(() => {
    window.addEventListener('resize', onResize)
    return () => {
      window.removeEventListener('resize', onResize)
    };
  }, [query]);

  return {
    greaterThanQuery
  }
}

export default useMediaQuery;
